import com.rameses.rcp.common.*;
import com.rameses.osiris2.common.*;

class HolidayEventInfoController extends CRUDController
{
    String serviceName = 'HolidayEventCalendarService';
    String entityName = 'holidayevent';

    String createFocusComponent = 'entity.date';
    String editFocusComponent = 'entity.name';            
    boolean showConfirmOnSave = false;
    boolean allowApprove = false;

    Map createPermission = [domain:'DATAMGMT', role:'DATAMGMT_AUTHOR', permission:'holidayevent.create']; 
    Map editPermission = [domain:'DATAMGMT', role:'DATAMGMT_AUTHOR', permission:'holidayevent.edit']; 
    Map deletePermission = [domain:'DATAMGMT', role:'DATAMGMT_AUTHOR', permission:'holidayevent.delete']; 
    Map approvePermission = [domain:'DATAMGMT', role:'DATAMGMT_AUTHOR', permission:'holidayevent.approve'];             
}
