import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class ApprovalOfferFormController
{
    String title = "Approval Offer Form"
    def loanamount
    def loanapp
    def entity
    def handler
    def absenceTypes = [
        [key:'1', value:'POLICY'], 
        [key:'2', value:'PROVISIONS']
    ]

    def init() {
        entity = [ policy:30 ]
    }

    def approve() {
        if( handler ) handler(entity)
        return "_close"
    }

    def cancel() {
        return "_close"
    }

    def mustCollateralHandler = [
        fetchList: {o->
            return []
        }
    ] as EditorListModel
}
