import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class BusinessFormController extends com.rameses.clfc.loan.controller.PopupMasterController
{
    def kinds = [
        [key:'1', value:'SARI-SARI STORE'], 
        [key:'2', value:'CARENDERIA'], 
        [key:'3', value:'OPERATOR'], 
        [key:'4', value:'OTHERS']
    ]
    def occupancyTypes = [
        [key:'1', value:'OWNED'], 
        [key:'2', value:'RENTED'], 
        [key:'3', value:'FREE USE'], 
        [key:'4', value:'OTHERS']
    ]
    def rentTypes = [
        [key:'1', value:'W/CONTRACT'], 
        [key:'2', value:'W/O CONTRACT']
    ]
    def ownerships = [
        [key:'1', value:'SOLE PROPRIETORSHIP'], 
        [key:'2', value:'PARTNERSHIP'], 
        [key:'3', value:'CORPORATION']
    ]

    public def approve() {
        if( entity.occupancy.type == 'RENTED' ) {
            if( !entity.occupancy.renttype ) throw new Exception("Rent Type is required.")
            if( !entity.occupancy.rentamount ) throw new Exception("Rent Amount is required.")
        }
        return super.approve()
    }
}
