import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class EmploymentFormController
{
    String title = "Employment Form"
    def entity = [:]
    def handler
    def statusList = [
        [key:'1', value:'REGULAR'], 
        [key:'2', value:'CONTRACTUAL'], 
        [key:'3', value:'PROBATIONARY'], 
        [key:'4', value:'RETIRED'], 
        [key:'5', value:'OTHERS']
    ]

    def init() {
        entity = [:]
    }

    def approve() {
        if( handler ) handler(entity)
        return "_close"
    }

    def cancel() {
        return "_close"
    }
}
