workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class MustCollateralFormController
{
    def entity
    def loanapp
    def handler

    def init() {
        entity = [
            appliancelist: copyList(loanapp.appliancelist)
        ]
    }

    def copyList( src ) {
        def list = []
        src.each{
            list.add(it)
        }
        return list
    }

    def approve() {
        if( handler ) handler(entity)
        return "_close"
    }

    def cancel() {
        return "_close"
    }

    def applianceHandler = [
        fetchList: {o->
            return entity.appliancelist
        }
    ] as EditorListModel

    def vehicleHandler = [
        fetchList: {o->
            return entity.vehiclelist
        }
    ] as EditorListModel

    def propertyHandler = [
        fetchList: {o->
            return entity.propertylist
        }
    ] as EditorListModel
}
