import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class OtherCollateralFormController
{
    String title = "Other Collateral Form"
    def entity
    def handler

    def init() {
        entity = [:]
    }

    def approve() {
        if( handler ) handler(entity)
        return "_close"
    }

    def cancel() {
        return "_close"
    }
}
