import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class OtherLendingFormController
{
    String title = "Other Lending Form"
    def entity
    def handler
    def modesOfPayment = [
        [key:'1', value:'DAILY'], 
        [key:'2', value:'WEEKLY'], 
        [key:'3', value:'MONTHLY']
    ]

    def init() {
        entity = [:]
    }

    def approve() {
        if( handler ) handler(entity)
        return "_close"
    }

    def cancel() {
        return "_close"
    }
}
