workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class SiblingInformationFormController
{
    String title = "Sibling Information Form"
    def entity
    def handler
    def selectedEmployment
    def selectedOtherIncome

    def init() {
        entity = [ employmentList:[], otherIncomeList:[] ]
    }

    def approve() {
        if( handler ) handler(entity)
        return "_close"
    }

    def cancel() {
        return "_close"
    }

    def addEmployment() {
        def handler = {employment->
            entity.employmentList.add(employment)
            employmentHandler.load()
        }
        return InvokerUtil.lookupOpener("employment:create", [handler:handler])
    }

    def employmentHandler = [
        fetchList: {o->
            return entity.employmentList
        },
        onRemoveItem: {o->
            if(MsgBox.confirm("You are about to remove this employment. Continue?")) {
                entity.employmentList.remove(o)
                return true
            }
            return false
        }
    ] as EditorListModel

    def addOtherIncome() {
        def handler = {otherincome->
            entity.otherIncomeList.add(otherincome)
            otherIncomeHandler.load()
        }
        return InvokerUtil.lookupOpener("otherincome:create", [handler:handler])
    }

    def otherIncomeHandler = [
        fetchList: {o->
            return entity.otherIncomeList
        },
        onRemoveItem: {o->
            if(MsgBox.confirm("You are about to remove this source of income. Continue?")) {
                entity.otherIncomeList.remove(o)
                otherIncomeHandler.load()
            }
            return false
        }
    ] as EditorListModel
}
