import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osirsi2.client.*;

class VehicleFormController
{
    String title = "Vehicle Form"
    def entity
    def handler
    def hasORCR
    def kinds = [
        [key:'1', value:'MOTORCYCLE'], 
        [key:'2', value:'PUJ'], 
        [key:'3', value:'OTHERS']
    ]
    def uses = [
        [key:'1', value:'FOR HIRE'], 
        [key:'2', value:'PRIVATE']
    ]

    def init() {
        entity = [:]
        hasORCR = true
    }

    def approve() {
        if( handler ) handler(entity)
        return "_close"
    }

    def cancel() {
        return "_close"
    }
}
