import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.common.*;

class PrincipalBorrowerLookup extends LookupController
{
    public String getServiceName() { return "PrincipalBorrowerService" }
    public Column[] getColumns() {
        return [
            new Column(name:'txnno', caption:'Customer No.'),
            new Column(name:'name', caption:'Name'),
            new Column(name:'address', caption:'Address')
        ] as Column[]
    }
}
