import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class SampleFormController
{
    def addAppliance() {
        def handler = { appliance->
            println 'appliance-> '+appliance
        }
        return InvokerUtil.lookupOpener("appliance:create", [handler:handler])
    }

    def addAttachments() {
        def handler = {attachment->
            println 'attachment-> '+attachment
        }
        return InvokerUtil.lookupOpener("attachment:create", [handler:handler])
    }

    def addCheckingAccount() {
        def handler = {checking->
            println 'checking-> '+checking
        }
        return InvokerUtil.lookupOpener("checking:create", [handler:handler])
    }

    def addSavingsAccount() {
        def handler = {savings->
            println 'savings-> '+savings
        }
        return InvokerUtil.lookupOpener("savings:create", [handler:handler])
    }

    def addChild() {
        def handler = {child->
            println 'child-> '+child
        }
        return InvokerUtil.lookupOpener("child:create", [handler:handler])
    }
}
